wire.script:
this.skipConfigurationDialog();
this.requestCustomConfiguration(async (item, requirements) => {
    const actor = item.actor;
    const maxSpellSlot = 4;

    function getSpellSlots(level) {
        return actor.system.spells[`spell${level}`];
    }

    console.log("About to smite");

    const { consumeSlot, spellLevel } = await new Promise(async (resolve, reject) => {
        // Get options for available slots
        let optionsText = "";
        for (let i = 1; i <= maxSpellSlot; i++) {
            const slots = getSpellSlots(i);
            if (slots.value > 0) {
                const level = CONFIG.DND5E.spellLevels[i];
                const label = game.i18n.format('DND5E.SpellLevelSlot', { level: level, n: slots.value });
                optionsText += `<option value="${i}">${label}</option>`;
            }
        }
        await new Dialog({
            title: "Divine Smite: Usage Configuration",
            content: `
                <form id="smite-use-form">
                    <p>` + game.i18n.format("DND5E.AbilityUseHint", { name: "Divine Smite", type: "feature" }) + `</p>
                    <div class="form-group">
                        <label>Spell Slot Level</label>
                        <div class="form-fields">
                            <select name="slot-level">` + optionsText + `</select>
                        </div>
                    </div>
                    <div class="form-group">
                        <label class="checkbox">
                        <input type="checkbox" name="consumeCheckbox" checked/>` + game.i18n.localize("DND5E.SpellCastConsume") + `</label>
                    </div>
                </form>
                `,
            buttons: {
                smite: {
                    icon: '<i class="fas fa-bolt"></i>',
                    label: "Smite",
                    callback: (html) => {
                        const spellLevel = parseInt(html.find('[name=slot-level]')[0].value);
                        const consumeSlot = html.find('[name=consumeCheckbox]')[0].checked;

                        resolve({ consumeSlot, spellLevel });
                    }
                }
            },
            default: "smite"
        }).render(true);
    });

    if (consumeSlot) {
        requirements.doConsumeSpellSlot = true;
        requirements.consumedSpellLevel = spellLevel;
    }

    return { spellLevel };
});

return this.applyDefaultTargetsAsEffective(
    this.applyEffects()
);
