wire.script:
this.setTemplateTargetSelection(false);

this.registerFlowStep("placeTemplate", true, async activation => {
    game.wire.runInQueue(() => {
        game.wire.placeTemplate({
            t: "circle",
            user: game.user.id,
            distance: 5,
            direction: 0,
            x: 0,
            y: 0,
            fillColor: game.user.color
        }).then(async () => {
            await activation.continue();
        });
    });

    await activation.wait();
});

const callBolt = this.performCustomStep("placeTemplate",
    this.confirmTargets(
        this.performSavingThrow(
            this.performSaveDamageRoll(
                this.sequence(
                    this.applyDamage()
                )
            )
        )
    )
)


return this.pick(
    this.isConditionTriggered(
        callBolt
    ),
    this.isImmediateApplication(
        this.applyConcentration(
            callBolt
        )
    )
);
