wire.script:
this.registerFlowStep("rollEffect", true, async (activation) => {
    const table = await fromUuid("Compendium.dnd5e.tables.LHEts1oDaDwcehuj");
    const { roll, results } = await table.draw({ displayChat: false });
    await game.dice3d?.showForRoll(roll, game.user, !game.user.isGM);
    const resultText = results[0].text;
    activation.assignCustomHtml(resultText);
});

this.registerFlowStep("removeTemplate", true, async (activation) => {
    await this.activation.template?.delete();
});

return this.pick(
    this.isDelayedApplication(
        this.performCustomStep("rollEffect")
    ),
    this.applyConcentration(
        this.confirmTargets(
            this.performSavingThrow(
                this.applyEffects(
                    this.performCustomStep("removeTemplate")
                )
            )
        )
    )
);
