wire.script:
this.skipTemplatePlacement();

this.registerFlowStep("placeBead", true, async (activation) => {
     const templateData = await game.wire.placeTemplate({
        t: "circle",
        user: game.user.id,
        distance: 2,
        direction: 0,
        x: 0,
        y: 0,
        fillColor: game.user.color
     });
     await activation.assignTemplateData(templateData);
     await activation.updateConfig({
         bead: {
             x: templateData.x,
             y: templateData.y
         }
     });
});

this.registerFlowStep("explode", true, async (activation) => {
    console.log("EXPLODE");
    // game.wire.runInQueue(async () => {
        // await activation.continue();
    // });
    // await activation.wait();
    const templateData = await game.wire.placeTemplate({
        t: "circle",
        user: game.user.id,
        distance: 20,
        direction: 0,
        x: activation.config.bead.x,
        y: activation.config.bead.y,
        fillColor: game.user.color
    }, activation.config, { selectTargets: true, interactive: false });
    await activation.assignTemplateData(templateData);
});

this.registerUpdater("Empower Blast", true, (condition, item, effect) => {
    effect.setFlag("wire", "activationConfig", mergeObject({ empowered: 1 }, effect.getFlag("wire", "activationConfig")));
});

return this.pick(
    this.isDelayedApplication(
        this.performCustomStep("explode",
            this.confirmTargets(
                this.removeTemplate(
                    this.performSavingThrow(
                        this.performSaveDamageRoll(
                            this.applyDamage()
                        )
                    )
                )
            )
        )
    ),
    this.applyConcentration(
        this.performCustomStep("placeBead")
    )
)
