wire.script:
this.skipConfigurationDialog();
this.requestCustomConfiguration(async (item, requirements) => {
    const actor = item.actor;
    const minSpellSlot = item.system.level;

    function getSpellSlots(level) {
        return actor.system.spells[`spell${level}`];
    }

    console.log("Configuring Flame Strike");

    const { consumeSlot, spellLevel, fireUpcast, radiantUpcast } = await new Promise(async (resolve, reject) => {
        // Get options for available slots
        let optionsText = "";
        for (let i = minSpellSlot; i <= 9; i++) {
            const slots = getSpellSlots(i);
            if (slots.value > 0) {
                const level = CONFIG.DND5E.spellLevels[i];
                const label = game.i18n.format('DND5E.SpellLevelSlot', { level: level, n: slots.value });
                optionsText += `<option value="${i}">${label}</option>`;
            }
        }

        let upcastAmount = 0;
        let radiantLevels = 0;

        await new Dialog({
            title: "Flame Strike: Usage Configuration",
            content: `
                <form id="flame-strike-use-form">
                    <p>` + game.i18n.format("DND5E.AbilityUseHint", { name: "Flame Strike", type: "spell" }) + `</p>
                    <div class="form-group">
                        <label>Spell Slot Level</label>
                        <div class="form-fields">
                            <select name="slot-level">` + optionsText + `</select>
                        </div>
                    </div>
                    <div style="border: 2px groove #eeede0; padding: 3px 7px; margin: 10px 0;">
                        <div class="form-group">
                            <label>Upcast bonus damage distribution <span class="units">(<span class="upcast-amount">${upcastAmount}</span> available)</span></label>
                        </div>
                        <div class="form-group">
                            <div class="form-fields flexrow" style="gap: 10px">
                                <div style="flex: 0; white-space: nowrap;">Fire <span class="fire-amount">0</span></div>
                                <input class="flex1" type="range" name="radiant-levels" value="0" min="0" max="${upcastAmount}" step="1" />
                                <div style="flex: 0; white-space: nowrap;">Radiant <span class="radiant-amount">0</span></div>
                            </div>
                        </div>
                    </div>
                    <div class="form-group">
                        <label class="checkbox">
                        <input type="checkbox" name="consumeCheckbox" checked/>` + game.i18n.localize("DND5E.SpellCastConsume") + `</label>
                    </div>
                </form>
                `,
            render: (html) => {
                function updateUI() {
                    html.find('.upcast-amount').text(upcastAmount);
                    html.find('.fire-amount').text(upcastAmount - radiantLevels);
                    html.find('.radiant-amount').text(radiantLevels);
                    html.find('input[name="radiant-levels"]').attr('max', upcastAmount);
                }

                html.find('select[name="slot-level"').change(function(event) {
                    upcastAmount = event.target.value - item.system.level;
                    radiantLevels = Math.min(radiantLevels, upcastAmount);
                    updateUI();
                });
                html.find('input[name="radiant-levels"]').change(function(event) {
                    radiantLevels = Math.min(event.target.value, upcastAmount);
                    updateUI();
                });
            },
            buttons: {
                cast: {
                    icon: '<i class="fas fa-magic"></i>',
                    label: "Cast Spell",
                    callback: (html) => {
                        const spellLevel = parseInt(html.find('[name=slot-level]')[0].value);
                        const consumeSlot = html.find('[name=consumeCheckbox]')[0].checked;
                        const fireUpcast = upcastAmount - radiantLevels;
                        const radiantUpcast = radiantLevels;

                        resolve({ consumeSlot, spellLevel, fireUpcast, radiantUpcast });
                    }
                }
            },
            default: "smite"
        }).render(true);
    });

    requirements.consumedSpellLevel = consumeSlot ? `spell${spellLevel}` : null;

    return { spellLevel, fireUpcast, radiantUpcast };
});

return this.defaultFlow();
