wire.script:
this.registerFlowStep("checkPetrification", true, async activation => {
    const counters = activation.config.counters || { success: 0, failure: 0};
    if (activation.effectiveTargets.length) {
        counters.failure++;
    } else {
        counters.success++;
    }
    await activation.updateConfig({ counters });

    await activation.assignCustomHtml(`
        <div style="text-align: center">
            <p>Successes <b>${counters.success}</b></p>
            <p>Failures <b>${counters.failure}</b></p>
        </div>
    `);

    if (counters.success >= 3 || counters.failure >= 3) {
        game.wire.runInQueue(async () => {
            await activation.sourceEffect.delete();
        });
    } else if (counters.failure < 3) {
        return false;
    }
});

return this.pick(
    this.isDelayedApplication(
        this.performSavingThrow(
            this.performCustomStep("checkPetrification",
                this.applyEffects()
            )
        )
    )
)
