wire.script:
this.registerUpdater("Check Hunter's Mark target", true, async (condition, item, effect, details) => {
    console.log("Checking Hunter's Mark target");
    const childUuids = effect.flags.wire?.childEffectUuids || [];
    const effectTarget = game.wire.fromUuid(childUuids[0]).parent;
    const attackTarget = game.user.targets.first()?.actor;
    const isTargetValid = childUuids.length && (attackTarget == effectTarget || effectTarget.system.attributes.hp.value > 0);

    if (!isTargetValid) {
        const doApply = await Dialog.confirm({ title: "Apply Hunter's Mark?", content: '<div class="dialog-bonus">Use bonus action to apply Hunter\'s Mark?</h1>' });
        if (doApply) {
            await game.wire.removeChildEffects(effect);
            await game.wire.createChildEffects(effect, "immediate", game.user.targets.first().actor);
        }
    }
});

this.registerUpdater("Apply Hunter's Mark damage", true, (condition, item, effect, details) => {
    const targetActor = game.user.targets.first().actor;
    if (targetActor.effects.find(e => e.origin === effect.origin)) {
        return "+1d6";
    }
});
