wire.script:
this.registerFlowStep("update", true, async (activation) => {
    const config = activation.config;
    const missilesCast = (config?.missilesCast || 0);
    const missilesAvailable = (config?.spellLevel || 1) + 2;

    await activation.assignCustomHtml(`Missile ${missilesCast+1} / ${missilesAvailable}`);
});

this.registerFlowStep("repeat", true, async (activation) => {
    const config = duplicate(activation.config);
    const missilesCast = (config?.missilesCast || 0) + 1;
    const missilesAvailable = (config?.spellLevel || 1) + 2;

    config.missilesCast = missilesCast;

    if (missilesCast < missilesAvailable) {
        await activation.spawnActivation("immediate", config);
    }
});

return this.performCustomStep("update",
    this.confirmTargets(
        this.applyDefaultTargetsAsEffective(
            this.performSaveDamageRoll(
                this.applyDamage(
                    this.performCustomStep("repeat")
                )
            )
        )
    )
);
