wire.script:
this.registerFlowStep("update", true, async (activation) => {
    const config = activation.config;
    const raysCast = (config?.raysCast || 0);
    const raysAvailable = (config?.spellLevel || 2) + 1;

    await activation.assignCustomHtml(`Ray ${raysCast+1} / ${raysAvailable}`);
});

this.registerFlowStep("repeat", true, async (activation) => {
    const config = duplicate(activation.config);
    const raysCast = (config?.raysCast || 0) + 1;
    const raysAvailable = (config?.spellLevel || 2) + 1;

    config.raysCast = raysCast;

    if (raysCast < raysAvailable) {
        await activation.spawnActivation("immediate", config);
    }
});

return this.performCustomStep("update",
    this.confirmTargets(
        this.performAttackRoll(
            this.performAttackDamageRoll(
                this.applyDamage(
                    this.attackCompleted(
                        this.performCustomStep("repeat")
                    )
                )
            )
        )
    )
);
