wire.script:
this.registerFlowStep("applySleep", true, async activation => {
    const points = activation.damageParts.result[0].roll.total;
    const actorsSorted = activation.allTargets.map(t => { return { actor: t.actor, hp: t.actor.system.attributes.hp.value }; }).sort((a, b) => a.hp - b.hp);

    let used = 0;
    let effective = actorsSorted.reduce((prev, cur) => {
        if (cur.hp <= points - used) {
            used += cur.hp;
            return [...prev, cur.actor];
        } else {
            return prev;
        }
    }, []);

    await activation.applyEffectiveTargets(effective);
});

return this.confirmTargets(
    this.performSaveDamageRoll(
        this.performCustomStep("applySleep",
            this.removeTemplate(
                this.applyEffects()
            )
        )
    )
);
