wire.script:
const attack = this.confirmTargets(
    this.performAttackRoll(
        this.performAttackDamageRoll(
            this.applyDamage(
                this.attackCompleted()
            )
        )
    )
)

return this.pick(
    this.isImmediateApplication(
        this.applyDurationEffect(
            attack
        )
    ),
    this.isDelayedApplication(
        attack
    )
)
