wire.script:
this.registerFlowStep("heal", true, async (activation) => {
    const item = activation.item;
    const target = activation.singleTarget.actor;

    const rolledDamage = await activation.damageParts.appliedToActor(item, target, activation.isTargetEffective(target));

    await game.wire.declareHealing(Math.floor(rolledDamage.damage * 0.5), activation.item.actor, activation.item.actor);
});

const attack = this.confirmTargets(
    this.performAttackRoll(
        this.performAttackDamageRoll(
            this.applyDamage(
                this.performCustomStep("heal")
            )
        )
    )
)

return this.pick(
    this.isConditionTriggered(
        attack
    ),
    this.applyConcentration(
        attack
    )
)
